import React from 'react';
import { Menu, Container, Button, Sidebar, Segment } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';
import SignedOutMenu from './SignedOutMenu';
import SignedInMenu from './SignedInMenu';
import { useSelector } from 'react-redux';
import { useState } from 'react';

export default function NavBar({ setFormOpen }) {
  const { authenticated } = useSelector((state) => state.auth);
  const [visible, setVisible] = useState(false);

  return (
    <Menu inverted fixed='top'>
      <Container>
        <Button />
        <Sidebar.Pushable as={Segment}>
          <Sidebar
          as={Menu}
          animation='overlay'
          icon='labeled'
          inverted
          onHide={() => setVisible(false)}
          vertical
          visible={visible}
          width='thin'>
          <Menu.Item as={NavLink} exact to='/' header>
            <img
              src='/assets/logo01.png'
              alt='logo'
              style={{ marginRight: 15 }}
            />
            ПРЕВОЗ
          </Menu.Item>
          <Menu.Item as={NavLink} to='/about' name='За Цветан Стојноавски' />
          <Menu.Item as={NavLink} to='/events' name='понуди' />

          {authenticated && (
            <Menu.Item as={NavLink} to='/createEvent'>
              <Button positive inverted content='Додај понуда' />
            </Menu.Item>
          )}
          </Sidebar>
        </Sidebar.Pushable>
        {authenticated ? <SignedInMenu /> : <SignedOutMenu />}
      </Container>
    </Menu>
  );
}
