import React from 'react';
import { Menu, Container, Button, Grid, GridRow } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';
import SignedOutMenu from './SignedOutMenu';
import SignedInMenu from './SignedInMenu';
import { useSelector } from 'react-redux';
import { useState } from 'react';

export default function NavBar({ setFormOpen }) {
  const { authenticated } = useSelector((state) => state.auth);
  const [showLinks, setShowLinks] = useState(false);

  return (
    <div className='Navbar'>
      <div className='Navbar__leftSide'>
        <div className='Navbar__leftSide__links'>
          <NavLink exact to='/' className='Navbar__leftSide__links__nav'>
            <img
              src='/assets/logo01.png'
              alt='logo'
              className='Navbar__leftSide__links__nav__img'
            />
            ПРЕВОЗ
          </NavLink>
          <NavLink to='/about' className='Navbar__leftSide__links__nav'>
            За авторот
          </NavLink>
          <NavLink to='/events' className='Navbar__leftSide__links__nav'>
            Понуди
          </NavLink>
          {authenticated && (
            <Menu.Item as={NavLink} to='/createEvent'>
              <Button
                positive
                inverted
                content='Додај понуда'
                style={{ marginLeft: 15 }}
              />
            </Menu.Item>
          )}
        </div>
        <button className='Navbar__leftSide__btn'>Open</button>
      </div>
      <div className='Navbar__rightSide'>
        {authenticated ? <SignedInMenu /> : <SignedOutMenu />}
      </div>
    </div>

    //   <Menu inverted fixed='top' className='Navbar'>
