import React from 'react';
import { Menu, Container, Button } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';
import SignedOutMenu from './SignedOutMenu';
import SignedInMenu from './SignedInMenu';
import { useSelector } from 'react-redux';

export default function NavBar({ setFormOpen }) {
  const {authenticated} = useSelector(state => state.auth);

  return (
    <Menu inverted fixed='top' stackable>
      <Container>
        <Menu.Item as={NavLink} exact to='/' header>
          <img src='/assets/logo01.png' alt='logo' style={{ marginRight: 15 }} />
          ПРЕВОЗ
        </Menu.Item>
        <Menu.Item as={NavLink} to='/about' name='За мене' />
        <Menu.Item as={NavLink} to='/events' name='понуди' />
        <Menu.Item as={NavLink} to='/sandbox' name='За превозот' />
        {authenticated && (
          <Menu.Item as={NavLink} to='/createEvent'>
            <Button positive inverted content='Додај понуда' />
          </Menu.Item>
        )}
        {authenticated ? (
          <SignedInMenu />
        ) : (
          <SignedOutMenu />
        )}
      </Container>
    </Menu>
  );
}
