import React from 'react';
import { Feed } from 'semantic-ui-react';
import {formatDistance} from 'date-fns';

export default function EventFeedItem({ post }) {
  let summary;
  switch (post.code) {
    case 'joined-event':
      summary = (
        <>
          <Link href={`/profile/${post.userUid}`}>{post.displayName} </Link> се пријави за патување кај <a href={`/events/${post.eventId}`}>{post.hostedBy}</a>
        </>
      );
      break;
    case 'left-event':
      summary = (
        <>
          <a href={`/profile/${post.userUid}`}>{post.displayName} </a> го откажа патувањето кај{' '}
          <a href={`/events/${post.eventId}`}>{post.hostedBy}</a>
        </>
      );
      break;
    default:
      summary = 'Нешто се случи';
      break;
  }

  return (
      <Feed.Event>
          <Feed.Label image={post.photoURL} />
          <Feed.Content>
            <Feed.Date>пред {formatDistance(new Date(post.date), new Date())} </Feed.Date>
            <Feed.Summary>
                {summary}
            </Feed.Summary>
          </Feed.Content>
      </Feed.Event>
  )
}
