import React, { useState } from 'react'
import { useEffect } from 'react';
import { Link } from 'react-router-dom';
// import { Header, Segment } from 'semantic-ui-react'


export default function About() {
    const [offsetY, setOffsetY] = useState(0);
    const handleScroll = () => setOffsetY(window.pageYOffset);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);

        return () => window.removeEventListener('scroll', handleScroll);
    },[]);

    const renderContent = () => (
        <>
        <div className='Parallax_content_heading'>
            <h1 className='Parallax__content__heading__text'>Цветан Стојановски</h1>
            <h2 className='Parallax__content__heading__caption'>Моите достигнувањеа во React</h2>
        </div>
        <div>
            <p>Оваа страница претставува презентација на она што јас до сега го имам достигнато во React. Доколку Ве интересира целиот код можете да го погледате на <Link to='https://github.com/Cvetan65/prevoz'>GitHub</Link></p>
        </div>


        </>
    );
    return (
        <section className='Parallax'>
           <div className='Parallax__background1'
           style={{ transform: `translateY(-${offsetY * 0.5}px)` }}
           />
           <div className='Parallax__background2'
           style={{ transform: `translateY(${offsetY * 0.8}px)` }}
           />
           <div className='Parallax__content'>{renderContent}</div>
        </section>

    )

}
